import React from 'react';
import 'antd/dist/antd.css';
import { Tag, Image, Layout, Button, Descriptions, PageHeader, Modal, InputNumber, message } from 'antd';
import contractInstance from '../../utils/config';
import web3 from '../../utils/InitWeb3';
import { FileProtectOutlined } from '@ant-design/icons';

const {Content} = Layout;

export default class MyAuctionCard extends React.Component{

    constructor(props){
        super(props);
        this.state = ({
            NFT: props.NFT,
            isConnected: false,
            address: "",
            buttonDisable: false,
            tagColor: "red",
            NFTState: "正在拍卖",
            modalVisible: true,
        })

        this.renderContent = this.renderContent.bind(this);
        this.approve = this.approve.bind(this);
    }

    async componentDidMount(){

        let accounts = await web3.eth.getAccounts()
        if (accounts.length == 0) {
            this.setState({
                isConnected: false
            })
        }
        else {
            this.setState({
                isConnected: true,
                address: accounts[0]
            })

            let NFT = this.state.NFT;
            
            if(NFT.onsale == true){
                this.setState({
                    NFTState: "拍卖中",
                    tagColor: "red",
                    buttonDisable: true,
                })
            }
            else if(NFT.setaside == true){
                this.setState({
                    NFTState: "拍卖结束（等待申领）",
                    tagColor: "green",
                    buttonDisable: false,
                })
            }
        }
    }
    
    renderContent = (column = 2) =>(
        <Descriptions size="small" column={column}>
            <Descriptions.Item label="名称">{this.state.NFT.name}</Descriptions.Item>
            <Descriptions.Item label="描述">{this.state.NFT.description}</Descriptions.Item>
            <Descriptions.Item label="起拍价格">{this.state.NFT.startprice}</Descriptions.Item>
            <Descriptions.Item label="当前拍卖最高价格">{this.state.NFT.currentprice}</Descriptions.Item>
            <Descriptions.Item label="当前拍卖最高出价者">{this.state.NFT.currentowner}</Descriptions.Item>
        </Descriptions>
    );

    //卖方许可函数
    async approve(){
        let NFT = this.state.NFT;
        console.log(NFT.id);
        try{
            let tx = await contractInstance.methods.sellerApprove(Number(NFT.id )).send({
                from: this.state.address,
                value: web3.utils.toWei('0.0000000001', 'ether'),
            })
            message.success('卖方确认交易成功');
            this.setstate({
                buttonDisable: true,
            });
            window.location.href = './Profile';
        }
        catch(e){
            console.log(e);
        }
    }

    render(){
        return(
            <div style={{boxShadow: "2px 2px 1px 2px #888", margin: "5px"}}>
                <PageHeader
                    title= {this.state.NFT.id}
                    tags={<Tag color={this.state.tagColor}>{this.state.NFTState}</Tag>}
                    extra={[
                        <Button key="1" type="primary" disabled={this.state.buttonDisable} onClick={this.approve}>
                            出售许可
                        </Button>
                    ]}>
                </PageHeader>
                <Image width={200} height={200} src={this.state.NFT.uri} />
                <Content>{this.renderContent()}</Content>
            </div>
        )
    }
}
